/* global describe:true, beforeEach:true, afterEach:true, it:true, expect: true */
"use strict";

describe("angular-ovh-mondial-relay", function () {

    var mondialRelayProvider;

    beforeEach(function () {
        // this module is generated by grunt task. we need to create it to satisfy dependencies
        angular.module("angular-ovh-mondial-relay-images", []);
    });

    beforeEach(angular.mock.module("angular-ovh-mondial-relay"));

    beforeEach(function () {
        // Initialize the service provider 
        // by injecting it to a fake module's config block
        var fakeModule = angular.module("test.ovh-angular-mondial-relay", function () {
            // Do nothing
        });
        fakeModule.config(function (_mondialRelayProvider_) {
            mondialRelayProvider = _mondialRelayProvider_;
        });

        // Initialize test.ovh-angular-mondial-relay injector
        module("angular-ovh-mondial-relay", "test.ovh-angular-mondial-relay");

    });

    describe("Provider", function () {
        it("should get the current translation path", angular.mock.inject(function () {
            expect(mondialRelayProvider.setTranslationPath()).toEqual("../bower_components/angular-ovh-mondial-relay/dist");
        }));

        it("should set the current translation path", angular.mock.inject(function () {
            mondialRelayProvider.setTranslationPath("/hello/world");
            expect(mondialRelayProvider.setTranslationPath()).toEqual("/hello/world");
        }));
    });
});
